import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    count:0,
    friends
  };

  removeFriend = id => {
    // Filter this.state.friends for friends if ids not equal keep them. think about javascript filter method
    const newFriends = this.state.friends.filter(friend => friend.id !== id);
        // as long as they r not equal keep them on the page...
    // Set this.state.friends equal to the new friends array
   this.setState({ friends: newFriends });
    this.setState({count: this.state.count + 1});

  };
/** 
  x = ()=> {
this.setState({count: this.state.count + 1 })
  }
**/
  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (

      <Wrapper>
        <h1>Click on an Image to raise your score, but don't click on an image twice!</h1>
        <Title>  Score: {this.state.count}   </Title>

        {this.state.friends.map(friend => (
          <FriendCard
            removeFriend={this.removeFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
            count={this.state.count}  //reveals the state count
            counter={this.x}


          />
        ))}

      </Wrapper>
    );
  }
}

export default App;
