import React from "react";

// Create a new component named "Math"
// Render one Math component in the place of each "?" mark
// Math should accept 3 props
// num1, operator, and num2
// Math should return a span tag displaying the result e.g.  19 + 341 = 360







function Calculator() {

  return (
    <div>
      <Math num1={13} operator={'+'} num2={13} />
      <Math num1={26} operator={'-'}  num2= {26} />
      <Math num1={10}  operator={'*'} num2={20} />

    </div>
  );
}


function Math(props) {
let value = props.operator; 
let equal = '';

if (value ==='+') {
       equal = props.num1+props.num2; 
  }
if (value ==='-') {
    equal = props.num1-props.num2; 
}
if (value==='*') {
  equal = props.num1*props.num2; 
}


  return <h1>{props.num1} {props.operator} {props.num2} = {equal}</h1>


}

export default Calculator;
