import React from "react";


// this is the props you recieved from App.js, do you need to import it from above though?

function List (props) {

  var x = props.groceries;   // this line will import the groceries array

var purchased = x.filter((item) => {       //filter if you want items that have been purchased!
return item.purchased; 
})

var notpurchased = x.filter((item) => {       //filter if you want items that have not been purchased!
  return !item.purchased; 
  })

var display = purchased.map((item) => 
 <li key={item.toString()}>{item} .) {item.name} </li>
);
 //be careful with braces in react  , it's better to use arrow funtions in reacts
 var notdisplay = notpurchased.map((item) => 
 <li key={item.toString()}>{item.id}.) {item.name} </li>
);

  return (
    <ul className="list-group">
    <h1>only purchased items here:</h1>

      {display}
    <h1>only not purchased items here:</h1>
     {notdisplay}
    </ul>

  );
}
   //   {props.groceries} // you cannot return the entire object like this.....you will have to return it to map thorugh it 
export default List;
