import React from "react";

// Create a new component named "Math"
// Render one Math component in the place of each "?" mark
// Math should accept 3 props
// num1, operator, and num2
// Math should return a span tag displaying the result e.g.  19 + 341 = 360
function Math (props) {
  const answer =  eval(` ${props.numb1} ${props.operators} ${props.numb2} `);
  return <div>{props.numb1} {props.operators} {props.numb2} = {answer} </div>;
}

function Calculator() {
  return (
    <div>
      <Math numb1={19} operator="+" numb2={341} />

    </div>
  );
}

export default Calculator;
