import React, {Component} from "react";
import "./style.css";




class FriendCard extends React.Component {

    render () {    
  return (

    <div className="card">   {/*this is how each card will look like */}

      <div className="img-container">
        <img alt={this.props.name} src={this.props.image} onClick= { this.props.counter}/>
      </div>

      <div className="content">
        <ul>
          <li>
            <strong>id :</strong> {this.props.id}
          </li>
         </ul>
      </div>
      <span onClick={() => this.props.removeFriend(this.props.id)} className="remove">    {/*check out this classname*/}
            x
      </span>
    </div>
  );
}

}
export default FriendCard;
